// @ts-expect-error
import { DataSyncController } from 'thin-backend';
export function loginWithEmailAndPassword(email, password) {
    const dataSyncController = DataSyncController.getInstance();
    return dataSyncController.sendMessage({ tag: 'LoginWithEmailAndPassword', email, password });
}
export function createUser(email, password) {
    const dataSyncController = DataSyncController.getInstance();
    return dataSyncController.sendMessage({ tag: 'CreateUser', email, password });
}
export function confirmUser(userId, token) {
    const dataSyncController = DataSyncController.getInstance();
    return dataSyncController.sendMessage({ tag: 'ConfirmUser', userId, token });
}
